level1.input_text_Python:
class Mob:
    def __init__(self, x, y):
        self.x = x
        self.y = y
    def go_up(self, n):
        self.y += n
    def go_down(self, n):
        self.y -= n
    def go_right(self, n):
        self.x += n
    def go_left(self, n):
        self.x -= n

mob = Mob(0, 0)

