    int x;
    int y;
};
void go_up(struct Mob *mob, int n) {
    mob->y += n;
}
void go_down(struct Mob *mob, int n) {
    mob->y -= n;
}
void go_right(struct Mob *mob, int n) {
    mob->x += n;
}
