        int y;
        Mob(int a, int b) {
            x = a;
            y = b;
        }
        void go_up(int n) {
            y += n;
        }
        void go_down(int n) {
            y -= n;
        }
        void go_right(int n) {
            x += n;
        }
        void go_left(int n) {
            x -= n;
        }
};
